class TimeCapsule {
    constructor() {
        this.capsules = [];
    }

    addMessage(message, openDate) {
        this.capsules.push({ message, openDate });
        return `Message scheduled for ${openDate}`;
    }

    getMessages() {
        const today = new Date();
        return this.capsules
            .filter(c => new Date(c.openDate) <= today)
            .map(c => c.message);
    }
}

module.exports = TimeCapsule;

